import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import '../styles/LoginECadastro.css';
import { Link } from 'react-router-dom';
import { useState } from "react";
import { useNavigate } from 'react-router-dom';

const RecuperacaoDeSenha = () => {

    const [NovaSenha1, setNovaSenha1] = useState('');
    const [NovaSenha2, setNovaSenha2] = useState('');
    const navigate = useNavigate();
    const trocarDeSenha = (e) => {
        e.preventDefault();
        setNovaSenha1(e.target.value);
        setNovaSenha2(e.target.value);
        if(NovaSenha1 == NovaSenha2){
            alert('Senha alterada com sucesso');
        navigate('/Login');
        }
        
    }


    return(
        <div>
            <Header />
            <section id="contact" class="main-bg section">
                <div class="main-content intro-content">
                    <div class="intro-text-content">
                        <div class="contact-form">
                            <form onSubmit={trocarDeSenha}>
                                <fieldset class="form-grid">
                                    <h2>Recuperação de senha</h2>
                                    <legend></legend>
                                    <div class="form-group">
                                        <label for="senha1">Insira a nova senha</label>
                                        <input type="password" onChange={(e) => setNovaSenha1} name="senha1" id="senha1" placeholder="Insira a nova senha"/>
                                    </div>
                                    <div class="form-group">
                                        <label for="senha2">Repita a nova senha</label>
                                        <input onChange={(e) => setNovaSenha2} type="password" name="senha2" id="senha2" placeholder="Insira a nova senha"/>
                                    </div>
                                    <div class="form-group">
                                        <button type="submit">Avançar</button>
                                    </div>
                                    <div class="form-group">
                                        <Link to='/Recuperacao' class='btn-voltar'>Voltar</Link>
                                    </div>
                                </fieldset>
                            </form>
                            
                        </div>
                    </div>
                </div>
            </section>
            <Footer />
        </div>
    );
};

export default RecuperacaoDeSenha;
